import logo from './logo.svg';
import './App.css';
import Titre from './App.css';
import { useState } from 'react';
import './data/data.csv';
import logotuto from './LogoTuto.png'
import React, { useEffect }  from 'react';
import { readRemoteFile } from 'react-papaparse';
import bg from './bg.jpg';

//import ReadRemoteFile from "./CsvJSON.js"



 let j = require('./data/data.json');


const runCallback = (cb) => {
  return cb();
};








function App() {

  const [ Data, setData ] = useState([]);
  var i = 0;
  const nombreMax = 16;

    var readData = () => {
      readRemoteFile('https://docs.google.com/spreadsheets/d/e/2PACX-1vRXi-TcKwaEYai8I5dCjOKlaMJ7AR7E5TCAq72muzObYC-YDHQahJvuvhGLYl3z0A/pub?gid=191056284&single=true&output=csv', {
        complete: (results) => {

          setData(results.data);
          console.log(results.data);
          console.log(results.data[2][0]);
          return(results.data);
        },

      } );

    };


   var AffichageRank = () => {

    var i = 1;


    //console.log(j.Data);
    useEffect(() => {
      readData();
      //console.log(Data.results.data);
      // Run! Like go get some data from an API.
    }, []);


    const row = [];
    for (var i = 0; i < nombreMax; i++) {

      row.push(<p class="rectangleRank">{i + 1}<p class="rectangleName">{}<p class="rectangleScore">{j.Data[i].POINTS}</p></p></p>);

    }
    return row;


  };


    return (   
    <div className="App">


     <header className="App-header">




       <img src={bg} className="App-bg"/>
       <img src={logotuto} className="App-logo"/>
       <h2 className="Titre">
         Affichage des scores en direct !
       </h2>
       <h4 className="TitreTableau">Nom de l'équipe</h4>
       <h4 className="ScoresTableau">Points</h4>
       <h4 className="NumeroTableau">N°</h4>


       {AffichageRank()}


      <a className= 'credits' href="https://fr.freepik.com/vecteurs/fond" style={{color:'#FFFFFF'}}>Fond vecteur créé par coolvector - fr.freepik.com</a>

     </header>

   </div>);

};



export default App ;
