import choses from './data/data.csv';
import React, { Component } from 'react';
import { readRemoteFile } from 'react-papaparse';




export default class ReadRemoteFile extends Component {
  handleClick = () => {
    readRemoteFile('https://docs.google.com/spreadsheets/d/e/2PACX-1vRXi-TcKwaEYai8I5dCjOKlaMJ7AR7E5TCAq72muzObYC-YDHQahJvuvhGLYl3z0A/pub?gid=191056284&single=true&output=csv', {
      complete: (results) => {
        console.log('Results:', results);
        console.log(results.data[1][0]);
      },
      
    });
    
  };
 
  render() {
    this.handleClick();
    
    return <div></div>;
  }
}


function download(content, fileName, contentType) {
  var a = document.createElement("a");
  var file = new Blob([content], {type: contentType});
  a.href = URL.createObjectURL(file);
  a.download = fileName;
  a.click();
}

const csv = [
  ["1-1", "1-2", "1-3"],
  ["2-1", "2-2", "2-3"]
]



//var csv is the CSV file with headers
function CsvJSON(csv){
  //*var csv = choses;
  var lines=csv.toString().split("\n");

  var result = [];

  var headers=lines[0].split(",");

  for(var i=1;i<lines.length;i++){

	  var obj = {};
	  var currentline=lines[i].split(",");

	  for(var j=0;j<headers.length;j++){
		  obj[headers[j]] = currentline[j];
	  }

	  result.push(obj);

  }
  console.log(csv);
  console.log(choses);
  
  //return result; //JavaScript object
  JSON.stringify(result); //JSON
  console.log(csv[1]);

  var csv = require('./data/data.json');
  console.log(csv);
  //download(result, 'json.txt', 'text/plain');
  return result;
  

}
//export default CsvJSON ;
